import classes from './Cart.module.css';
import Modal from '../UI/Modal/Modal';
import { Fragment, useContext, useState } from 'react';
import CartContext from '../../store/cart-context';
import CartItem from './CartItem/CartItem';
import Checkout from '../CheckoutForm/Checkout';

const Cart = (props) =>
{
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [didSubmit, setDidSubmit] = useState(false);
    const cartCtx = useContext(CartContext);
    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
    const hasItems = cartCtx.items.length > 0;
    const cartItemRemoveHandler = id =>
    {
        cartCtx.removeItem(id);
    }

    const cartItemAddHandler = item =>
    {
        cartCtx.addItem({...item, amount: 1});
    }

    const formSubmitHandler = async (userData) =>
    {
        setIsSubmitting(true);
        await fetch('https://foodapp-2c7e1-default-rtdb.firebaseio.com/orders.json', {
            method: 'POST',
            body: JSON.stringify({
                user: userData,
                orderedItems: cartCtx.items
            })
        });
        setIsSubmitting(false);
        setDidSubmit(true);
    }
    const cartItems = <ul className={classes['cart-items']}>{cartCtx.items.map((item) => <CartItem key={item.id} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler.bind(null, item.id)} onAdd={cartItemAddHandler.bind(null, item)}/>)}</ul>;

    const cartModalContent = (
    <Fragment>
        {cartItems}
        <div className={classes.total}>
            <span>Total Amount</span>
            <span>{totalAmount}</span>
        </div>
        <div className={classes.action}>
            <button className={classes['button--alt']} onClick={props.onClose}>Close</button>
            {hasItems && <button className={classes.button}>Order</button>}
        </div>
        <Checkout onCancel={props.onClose} onSubmitHandler={formSubmitHandler}/>
    </Fragment>);

    const submittingModalContent = <p>Sending order data...</p>;
    const submitModalContent = <p>Sending order data...</p>;


    return (
        <Modal onClose={props.onClose}>
            {cartModalContent}
        </Modal>
    );
}

export default Cart;
